INSERT INTO `wp_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (2, 1, '2013-03-28 08:32:01', '2013-03-28 08:32:01', 'Detta är en exempelsida. Den skiljer sig från ett blogginlägg eftersom den är statisk och kommer att finnas på samma plats hela tiden, den kommer troligen att visas i din sidnavigering (i de flesta teman). De flesta börjar med en Om-sida som introducerar dem för potentiella besökare. Den skulle t.ex kunna ha följande innehåll: \r\n\r\n<blockquote> Hej där! Jag är cykelbud på dagen, blivande skådespelare på natten och detta är min blogg. Jag bor i Örebro, har en katt som heter Lurv och jag gillar Pina Coladas. (och att simma i Göta kanal).</blockquote>\r\n\r\n... eller något liknande detta:\r\n\r\n<blockquote>Den officiella svenska portalen för WordPress, WordPress Sverige grundades 2007 och tillhandahåller svenska översättningar för WordPress, teman och tillägg. Behöver man hjälp med att komma igång eller stöter på problem så är WordPress Sverige den perfekta platsen att få svensk support på. Du hittar portalen på wpsv.se.</blockquote>\r\n\r\nSom ny WordPress-användare bör du gå till <a href=\"http://gillis.wordpress-hemsida.se/wp-admin/\">din adminpanel</a> för att ta bort denna sida och sedan skapa nya sidor för ditt innehåll. Ha det så kul!\r\n', 'Home', '', 'publish', 'closed', 'open', '', 'exempelsida', '', '', '2013-05-23 11:20:09', '2013-05-23 09:20:09', '', 0, 'http://gillis.wordpress-hemsida.se/?page_id=2', 0, 'page', '', 0)#endQuery
