INSERT INTO `wp_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (12, 1, '2013-03-28 10:47:22', '2013-03-28 09:47:22', '<p style=\"text-align: justify;\"><span style=\"color: #ffffff;\">Born and raised in Stockholm, Sweden, Niclas Gillis first directed web commercials for various Swedish companies and worked as a trailer producer for European television at<a href=\"http://www.mtg.se/en/brands--operations/brands/pay-tv/viasat/\" target=\"_blank\"><span style=\"color: #ffffff;\"> The Modern Times Group</span></a> in the UK. </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff;\">Since moving to New York in 2009, Gillis has worked as a short-format filmmaker in both artistic and commercial endeavors, and as an actor in both Swedish and American feature films. He is a reader for <a href=\"http://www.universalpicturesinternational.com/\" target=\"_blank\"><span style=\"color: #ffffff;\">Universal Pictures International</span></a>, and a contributing writer for <em><a href=\"http://thelast-magazine.com/\" target=\"_blank\"><span style=\"color: #ffffff;\">The Last Magazine</span></a>. </em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff;\"><em></em>Gillis<em></em> is represented as an actor by Untitled Entertainment, and as a director by German creative agency <a href=\"http://www.gangofberlin.com/\" target=\"_blank\"><span style=\"color: #ffffff;\">Gang of Berlin</span></a>.</span></p>', 'About', '', 'publish', 'closed', 'open', '', 'about', '', '', '2014-08-28 16:49:11', '2014-08-28 14:49:11', '', 0, 'http://gillis.wordpress-hemsida.se/?page_id=12', 0, 'page', '', 0)#endQuery
