INSERT INTO `wp_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (530, 2, '2013-10-17 18:23:58', '2013-10-17 16:23:58', '<p style=\"text-align: justify;\"><span style=\"color: #00ff00;\">Niclas Gillis is a Swedish-born, New York-based writer and director.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #00ff00;\">Before moving to the US in 2009, Gillis directed web commercials for various Swedish companies and worked as a trailer producer for European television in both Sweden and the UK. Since relocating to New York, he has worked as a short-format filmmaker in both artistic and commercial endeavors, as well as an actor in both Swedish and American feature films. He is a reader for Universal Pictures International, and a contributing writer for <em>The Last Magazine.</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #00ff00;\">Gillis is represented as a filmmaker by German creative agency Gang of Berlin, and as an actor by Untitled Entertainment in Los Angeles.</span></p>', 'About', '', 'inherit', 'closed', 'closed', '', '12-revision-v1', '', '', '2013-10-17 18:23:58', '2013-10-17 16:23:58', '', 12, 'http://gillis.wordpress-hemsida.se/12-revision-v1/', 0, 'revision', '', 0)#endQuery
