INSERT INTO `wp_xa32n7v80g_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (12, 1, '2013-03-28 10:47:22', '2013-03-28 09:47:22', '<p style=\"text-align: justify;\"><span style=\"color: #ffffff;\">Born and raised in Stockholm, Sweden, Niclas Gillis first directed web commercials for various Swedish companies and worked as a trailer producer for European television at<a href=\"http://www.mtg.se/en/brands--operations/brands/pay-tv/viasat/\" target=\"_blank\"><span style=\"color: #ffffff;\"> The Modern Times Group</span></a> in the UK. </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff;\">Since moving to New York in 2009, Gillis has worked as a short-format filmmaker in both artistic and commercial endeavors, and as an actor in both Swedish and American feature films. He is a story analyst for <a href=\"http://www.universalpicturesinternational.com/\" target=\"_blank\"><span style=\"color: #ffffff;\">Universal Pictures International</span></a>, and a contributing writer for <em><a href=\"http://thelast-magazine.com/\" target=\"_blank\"><span style=\"color: #ffffff;\">The Last Magazine</span></a>. </em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff;\">Gillis is represented as an actor by Untitled Entertainment and as a director by <a href=\"http://moremedia.tv/\" target=\"_blank\"><span style=\"color: #ffffff;\">More Media</span></a>.</span></p>', 'About', '', 'publish', 'closed', 'open', '', 'about', '', '', '2015-09-24 14:54:15', '2015-09-24 18:54:15', '', 0, 'http://gillis.wordpress-hemsida.se/?page_id=12', 0, 'page', '', 0)#endQuery
INSERT INTO `wp_xa32n7v80g_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (400, 1, '2013-06-05 11:36:44', '2013-06-05 09:36:44', '<h2 style=\"text-align: center;\"></h2>\r\n<h2 style=\"text-align: center;\"></h2>\r\n<h2 style=\"text-align: center;\"></h2>\r\n<h2 style=\"text-align: center;\">MK-Ultra</h2>\r\n<p style=\"text-align: center;\"><span style=\"color: #c0c0c0;\">Written, directed, produced, acted in, and edited by Niclas Gillis in 2012.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"color: #c0c0c0;\">coming soon</span></p>', 'MK-Ultra', '', 'inherit', 'closed', 'closed', '', '297-revision-v1', '', '', '2013-06-05 11:36:44', '2013-06-05 09:36:44', '', 297, 'http://gillis.wordpress-hemsida.se/297-revision-7/', 0, 'revision', '', 0)#endQuery
