INSERT INTO `wp_xa32n7v80g_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (171, 1, '2013-03-28 08:32:01', '2013-03-28 08:32:01', 'Detta r en exempelsida. Den skiljer sig frn ett blogginlgg eftersom den r statisk och kommer att finnas p samma plats hela tiden, den kommer troligen att visas i din sidnavigering (i de flesta teman). De flesta brjar med en Om-sida som introducerar dem fr potentiella beskare. Den skulle t.ex kunna ha fljande innehll: \n\n<blockquote> Hej dr! Jag r cykelbud p dagen, blivande skdespelare p natten och detta r min blogg. Jag bor i rebro, har en katt som heter Lurv och jag gillar Pina Coladas. (och att simma i Gta kanal).</blockquote>\n\n... eller ngot liknande detta:\n\n<blockquote>Den officiella svenska portalen fr WordPress, WordPress Sverige grundades 2007 och tillhandahller svenska versttningar fr WordPress, teman och tillgg. Behver man hjlp med att komma igng eller stter p problem s r WordPress Sverige den perfekta platsen att f svensk support p. Du hittar portalen p wpsv.se.</blockquote>\n\nSom ny WordPress-anvndare br du g till <a href=\"http://gillis.wordpress-hemsida.se/wp-admin/\">din adminpanel</a> fr att ta bort denna sida och sedan skapa nya sidor fr ditt innehll. Ha det s kul!', 'Exempelsida', '', 'inherit', 'closed', 'closed', '', '2-revision-v1', '', '', '2013-03-28 08:32:01', '2013-03-28 08:32:01', '', 2, 'http://gillis.wordpress-hemsida.se/2-revision/', 0, 'revision', '', 0)#endQuery
