INSERT INTO `wp_xa32n7v80g_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (429, 1, '2013-10-11 15:08:12', '2013-10-11 13:08:12', '<style type=\"text/css\"><!--\nP { margin-bottom: 0.21cm; }\n--></style><span style=\"color: #000000;\"><span style=\"font-family: Arial;\"><span style=\"font-size: small;\">Niclas Gillis is a Swedish-born, New York-based writer and director. </span></span></span>\n\n<span style=\"color: #000000;\"><span style=\"font-family: Arial;\"><span style=\"font-size: small;\">Before moving to the US in 2009, Gillis directed web commercials for various Swedish companies and worked as a trailer producer for European television in both Sweden and the UK. Since relocating to New York, he has worked as a short-format filmmaker in both artistic and commercial endeavors, as well as an actor in both Swedish and American feature films. He is a reader for Universal Pictures International, and a contributing writer for </span></span><span style=\"font-family: Arial;\"><span style=\"font-size: small;\"><i>The Last Magazine.\n\nGillis is represented as a filmmaker by German creative agency Gang of Berlin, and as an actor by Untitled Entertainment in Los Angeles.', 'About', '', 'inherit', 'closed', 'closed', '', '12-autosave-v1', '', '', '2013-10-11 15:08:12', '2013-10-11 13:08:12', '', 12, 'http://gillis.wordpress-hemsida.se/12-autosave-v1/', 0, 'revision', '', 0)#endQuery
