INSERT INTO `wp_xa32n7v80g_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (318, 1, '2013-05-29 13:24:41', '2013-05-29 11:24:41', 'Niclas Gillis is a Swedish-born, New York-based writer and director.\r\n\r\nBefore moving to the US in 2009, Gillis directetweb commercials for various Swedish companies and worked as a trailer producer for European television in both Sweden and the UK.\r\n\r\nMoving to New York at the age of 19, Gillis has since worked as a short-format filmmaker in both artistic and commercial endeavors. He has worked as an actor in feature films, a reader on major screenplays, and as a writer for The Last magazine.\r\n\r\nGillis is represented as a filmmaker by German creative agency, Gang of Berlin, and as an actor by Untitled Entertainment in Los Angeles.', 'About', '', 'inherit', 'closed', 'closed', '', '12-revision-v1', '', '', '2013-05-29 13:24:41', '2013-05-29 11:24:41', '', 12, 'http://gillis.wordpress-hemsida.se/12-revision-9/', 0, 'revision', '', 0)#endQuery
