INSERT INTO `wp_xa32n7v80g_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (703, 2, '2014-09-16 05:02:34', '2014-09-16 03:02:34', '<iframe src=\"//player.vimeo.com/video/99372910?color= 1b1b1b\" height=\"355\" width=\"630\" allowfullscreen=\"\" frameborder=\"0\"></iframe>\r\n<h2 style=\"text-align: center;\">A Portrait of Ian Hylton</h2>\r\n<p style=\"text-align: center;\"><span style=\"color: #999999;\">Directed, shot, and edited by Niclas Gillis in 2013.</span></p>\r\n<div style=\"color: white !important; text-align: center; font-size: 20px\">For the bottom</div>', 'A Portrait of Ian Hylton ', '', 'inherit', 'closed', 'closed', '', '648-revision-v1', '', '', '2014-09-16 05:02:34', '2014-09-16 03:02:34', '', 648, 'http://niclasgillis.com/648-revision-v1/', 0, 'revision', '', 0)#endQuery
